fields.css:
#menu ul li a:hover, #menu ul li:hover a{background-color:#ae0001;color:white;}
#masthead h2 {font-size:40px; margin:60px 0 0 0; padding-bottom:0; color:#ae0001;}
h1 {
    border-bottom:1px solid grey;
    border-top:1px solid grey;
    padding:20px 0 0;
}
h1, h2, h4 {line-height: 1;}
#menuWrap {border-top:1px grey solid;}
.footer {border-top:4px #ae0001 solid;}
#menuWrap {border-bottom:1px grey solid;}
#menuWrap form {margin-top:8px; text-align:right;}
#menuWrap form input {border:none;}
#mainWrap, #sidebarWrap {background-color:white;}
#main, #sidebar {padding:15px;}
#menu ul li div{border:1px grey solid;border-top:none;background-color:white;}
#menu a, #menuitems li a {font-size:14px; font-weight:bold; text-transform:uppercase; text-decoration:none;}
#menuitems li a:hover {text-decoration:underline;}
#menu ul ul li a {font-size:13px; text-transform:none; color:black;}
#menu ul ul li a:hover{color:#ae0001; background-color:white;}
a {color:#ae0001;}
a.lightlink {color:black;}

.shaded {background-color:#e0e0e0;}

a#viewCart {margin-right:12px;}

a.add {
background-color:#ae0001;
color:white;
-moz-border-radius:4px;
-webkit-border-radius:4px;
}
a.add.top {font-weight:bold; text-transform:uppercase; text-decoration:none; margin-bottom:10px;}

#orderForm {
    padding:20px;
    border:4px #ae0001 solid;
    margin-bottom:15px;
}
#orderForm h3 {
    margin-bottom:5px;
}
form input {
    border: 1px grey solid;
}

#submit {
background-color:#ae0001;
color:white;
-moz-border-radius:4px;
-webkit-border-radius:4px;
border:none;
float:right;
clear:right;
}

#orderPreview td {border-top:1px grey solid;}
#orderPreview td ul {margin-bottom:0;}
#orderPreview tr.total td {border-top:4px #ae0001 solid;}
#orderPreview td.remove, #orderPreview tr.total td.remove {border-top:none;}

a.button {font-size:16px; padding:4px; text-decoration:none;
background-color:#ae0001;
color:white;
-moz-border-radius:5px;
-webkit-border-radius:5px;
}

a.button:hover {text-decoration:underline;}

form.send label {display:block;}

